import React from 'react'
import MenuItem from '../components/Menu/MenuItem'
import { useState } from 'react';
import { useEffect } from 'react';
import { array } from 'prop-types';


const Menu = () => {

    const [loadData, setLoadData] = useState(false);
    const [menu, setMenu] = useState(
         [{
            id: "1", 
            name: "Amila nuwan",
            price: 600
        },
        {
            id: "2",
            name: "nuwan", 
            price: 600
        }
    ]);

    useEffect(()=>{
        fetch("http://localhost:8070/Food/menu")
        .then((response)=>{
            return response.json();
        })
        .then(responseData=>{
            setMenu((menu)=>[...menu, responseData.
                for (i=0,i<=array.length,i++)
                        [i]]);
        })

    },
          [loadData]);


    

    const MenuList = ()=>{
        return menu.map((aName)=>{

            return(
                <MenuItem 
                          key ={aName.id} 
                          name = {aName.name}
                          price = {aName.price}  />
            )
        })
    };

    const menuHandler=()=>{
      setLoadData(true);
    };

    return (
        <React.Fragment>
            <h1>Menu</h1>

           <button onClick={menuHandler}>Add New</button>
           <button>obapan</button>
            <MenuList/>
            
        </React.Fragment>
    )
}

export default Menu
