import React from 'react'
import MenuItem from '../components/Menu/MenuItem'
import { useState } from 'react';
import { useEffect } from 'react';


const Menu = () => {
  
    const [menu, setMenu] = useState(
         [{
            id: 1, 
            name: "Amila nuwan",
            price: 600
        },
        {
            id: 2,
            name: "nuwan", 
            price: 600
        }
    ]);

    useEffect(()=>{
        fetch("http://localhost:8070/Food/menu")
        .then((response)=>{
            return response.json();
        })
        .then(responseData=>{
            return ([responseData.results[0]]);
        })

    },
          [])


    

    const MenuList = ()=>{
        return menu.map((aName)=>{

            return(
                <MenuItem 
                          key ={aName.id} 
                          name = {aName.name}
                          price = {aName.price}  />
            )
        })
    };

    const menuHandler=()=>{
        const newItem = {
            id: new Date(),
            name: 'ambaru',
            price: 1000
        }

        setMenu((menu) => [...menu, newItem]);
    };

    return (
        <React.Fragment>
            <h1>Menu</h1>

            <MenuList/>
            
        </React.Fragment>
    )
}

export default Menu
