import React from 'react'
import MenuItem from '../components/Menu/MenuItem'
import { useState } from 'react';
import { useEffect } from 'react';


const Menu = () => {

    const [loadData, setLoadData] = useState(false);
    const [menu, setMenu] = useState([]);

    useEffect(()=>{
        fetch("http://localhost:8070/Food/menu")
        .then((response)=>{
            return response.json();
        })
        .then(responseData=>{
            setMenu((menu)=>[...menu,responseData]);
                
        })

    },
          [loadData]);


    

    const MenuList = ()=>{
        return menu.map((aName)=>{

            return(
                <MenuItem 
                          key ={aName.id} 
                          name = {aName.name}
                          price = {aName.price}  />
            )
        })
    };

    const menuHandler=()=>{
      setLoadData(true);
    };

    return (
        <React.Fragment>
            <h1>Menu</h1>

           <button>obapan</button>
            <MenuList/>
            
           <button onClick={menuHandler}>Add New</button>
            
        </React.Fragment>
    )
}

export default Menu
